import React from "react";

const Hero = ({ langData, onShowForm }) => {
  return (
    <section
      className="relative h-screen w-full bg-cover bg-center"
      style={{ backgroundImage: "url('/Farmer-using-Phone.jpg')" }}
    >
      {/* üåë LIGHTER DARK OVERLAY (image zyada visible) */}
      <div className="absolute inset-0 bg-black/30" />

      {/* üåü CONTENT */}
      <div className="relative z-10 h-full flex items-center px-6 lg:px-20">
        <div
          className="
            bg-white/10 backdrop-blur-md
            border border-white/20
            p-12 rounded-3xl
            text-white max-w-xl
            shadow-2xl
            animate-[fadeUp_0.9s_ease-out]
          "
        >
          <h2 className="text-5xl md:text-7xl font-extrabold mb-6 leading-tight">
            {langData.heroTitle || "KrishiSakhi"}
          </h2>

          <p className="text-lg md:text-xl text-gray-200 mb-8">
            Empowering farmers with smart tools, local language support,
            and real-time insights.
          </p>

          <button
            className="
              bg-green-600 px-10 py-4 rounded-full
              text-xl font-bold
              hover:bg-green-700
              transition-all duration-300
              hover:scale-105
              active:scale-95
            "
            onClick={onShowForm}
          >
            {langData.heroBtn || "Helping Farmers Build Better"}
          </button>
        </div>
      </div>

      {/* ‚¨áÔ∏è SCROLL INDICATOR */}
      <div className="absolute bottom-10 left-1/2 -translate-x-1/2 animate-bounce">
        <i className="fa-solid fa-chevron-down text-white opacity-70 text-xl"></i>
      </div>

      {/* üé¨ CUSTOM ANIMATION */}
      <style>
        {`
          @keyframes fadeUp {
            0% {
              opacity: 0;
              transform: translateY(30px);
            }
            100% {
              opacity: 1;
              transform: translateY(0);
            }
          }
        `}
      </style>
    </section>
  );
};

export default Hero;
