import React, { useEffect, useState } from "react";

const languageMap = {
  en: "English", hi: "Hindi", pa: "Punjabi", mr: "Marathi", gu: "Gujarati",
  bn: "Bengali", ta: "Tamil", te: "Telugu", kn: "Kannada", ml: "Malayalam",
  or: "Odia", as: "Assamese", ur: "Urdu", sd: "Sindhi", sa: "Sanskrit",
  ks: "Kashmiri", kok: "Konkani", mai: "Maithili", ne: "Nepali",
};

const allLangCodes = Object.keys(languageMap);

const Header = ({ langData, currentLang, onLangChange, onNavigate, currentPage }) => {

  const [scrolled, setScrolled] = useState(false);

  // ðŸ”¹ Detect scroll
  useEffect(() => {
    const onScroll = () => {
      setScrolled(window.scrollY > 60);
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const handleNavClick = (e, page) => {
    e.preventDefault();
    onNavigate(page);
  };

  const navItemClass = (page) =>
    `relative px-2 py-1 font-medium transition-all duration-300
     ${currentPage === page
        ? scrolled
          ? "text-green-600 after:absolute after:left-0 after:-bottom-1 after:w-full after:h-[2px] after:bg-green-600"
          : "text-green-300 after:absolute after:left-0 after:-bottom-1 after:w-full after:h-[2px] after:bg-green-300"
        : scrolled
          ? "text-gray-700 hover:text-green-600 after:absolute after:left-0 after:-bottom-1 after:w-0 after:h-[2px] after:bg-green-600 hover:after:w-full after:transition-all after:duration-300"
          : "text-white hover:text-green-300 after:absolute after:left-0 after:-bottom-1 after:w-0 after:h-[2px] after:bg-green-300 hover:after:w-full after:transition-all after:duration-300"
     }`;

  return (
    <header
      className={`sticky top-0 z-30 w-full transition-all duration-500
        ${scrolled ? "bg-white/90 backdrop-blur-lg shadow-md" : ""}
      `}
      style={!scrolled ? {
        backgroundImage: "url('/images/farm-bg.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
      } : {}}
    >
      {/* Dark overlay only when NOT scrolled */}
      {!scrolled && <div className="absolute inset-0 bg-black/40"></div>}

      <nav
        className={`relative flex justify-between items-center max-w-7xl mx-auto px-6 py-4
        transition-all duration-500
        ${scrolled
          ? "rounded-none"
          : "rounded-full mt-4 mb-4 bg-white/20 backdrop-blur-xl border border-white/20 shadow-xl"
        }`}
      >

        {/* ðŸ”¹ LOGO */}
        <div
          onClick={(e) => handleNavClick(e, "home")}
          className={`flex items-center gap-3 cursor-pointer px-5 py-2 rounded-full
          transition-all duration-300 shadow-md
          ${scrolled
            ? "bg-green-600 hover:bg-green-700"
            : "bg-gradient-to-r from-green-500 to-green-600 hover:scale-105"
          }`}
        >
          <div className="bg-white text-green-600 rounded-full w-11 h-11 flex items-center justify-center shadow">
            <i className="fa-solid fa-leaf text-xl"></i>
          </div>
          <h1 className="font-poppins text-xl font-semibold text-white tracking-wide">
            KrishiSakhi
          </h1>
        </div>

        {/* ðŸ”¹ NAV LINKS */}
        <ul className="hidden lg:flex items-center gap-8">
          <li><button onClick={(e) => handleNavClick(e, "home")} className={navItemClass("home")}>{langData.home}</button></li>
          <li><button onClick={(e) => handleNavClick(e, "my-farm")} className={navItemClass("my-farm")}>{langData.myFarm}</button></li>
          <li><button onClick={(e) => handleNavClick(e, "todo")} className={navItemClass("todo")}>{langData.todo}</button></li>
          <li><button onClick={(e) => handleNavClick(e, "trend")} className={navItemClass("trend")}>{langData.trend}</button></li>
          <li><button onClick={(e) => handleNavClick(e, "feedback")} className={navItemClass("feedback")}>{langData.feedback}</button></li>
        </ul>

        {/* ðŸ”¹ RIGHT SIDE */}
        <div className={`flex items-center gap-4 ${scrolled ? "text-gray-700" : "text-white"}`}>

          {/* SEARCH */}
          <div className="relative hidden sm:block">
            <input
              type="text"
              placeholder={langData.search}
              className={`rounded-full pl-4 pr-10 py-2 text-sm w-44
              focus:outline-none transition
              ${scrolled
                ? "border border-gray-300 focus:border-green-600"
                : "bg-white/80 text-black focus:ring-2 focus:ring-green-400"
              }`}
            />
            <i className="fa-solid fa-magnifying-glass absolute right-3 top-1/2 -translate-y-1/2 text-gray-500"></i>
          </div>

          {/* LANGUAGE */}
          <select
            className={`rounded-full px-4 py-2 text-sm transition
            ${scrolled
              ? "border border-gray-300 text-black"
              : "bg-white/80 text-black"
            }`}
            value={currentLang}
            onChange={(e) => onLangChange(e.target.value)}
          >
            {allLangCodes.map((lang) => (
              <option key={lang} value={lang}>{languageMap[lang]}</option>
            ))}
          </select>

          {/* ACCESSIBILITY */}
          <button className="hover:text-green-600 transition">
            <i className="fa-solid fa-universal-access text-xl"></i>
          </button>
        </div>
      </nav>
    </header>
  );
};

export default Header;
