import React, { useState, useCallback } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import FormSection from './components/FormSection';
import Footer from './components/Footer';
import ChatbotResult from './components/ChatbotResult';

// Naye components
import MyFarm from './components/MyFarm';
import ToDoList from './components/ToDoList';
import LocalTrend from './components/LocalTrend';
import Feedback from './components/Feedback';
import { translations } from './data/translations';

const supportedLangs = Object.keys(translations);

function App() {
    const [currentLang, setCurrentLang] = useState('en');
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [currentPage, setCurrentPage] = useState('home'); 

    const handleLanguageChange = useCallback((newLang) => {
        if (supportedLangs.includes(newLang)) {
            setCurrentLang(newLang);
            document.documentElement.lang = newLang;
        }
    }, []);

    const navigateToResults = () => {
        setCurrentPage('results');
        setIsFormVisible(false);
    };

    const langData = translations[currentLang] || translations.en;

    // Smooth scroll + scroll-margin-top fix
    const handleNavigate = (page) => {
        setCurrentPage(page);
        const element = document.getElementById(page);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth', block: 'start' });
        } else {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }
    };

    const renderPage = () => {
        switch(currentPage) {
            case 'my-farm': 
                return <MyFarm id="my-farm" langData={langData} className="scroll-mt-24" />;
            case 'todo': 
                return <ToDoList id="todo" langData={langData} className="scroll-mt-24" />;
            case 'trend': 
                return <LocalTrend id="trend" langData={langData} className="scroll-mt-24" />;
            case 'feedback': 
                return <Feedback id="feedback" langData={langData} className="scroll-mt-24" />;
            case 'results': 
                return <ChatbotResult langData={langData} />;
            default: // Home Page
                return (
                    <>
                        <Hero 
                            langData={langData}
                            onShowForm={() => {
                                setIsFormVisible(true);
                                setTimeout(() => {
                                    document.getElementById('form-section')?.scrollIntoView({ behavior: 'smooth' });
                                }, 100);
                            }}
                        />
                        {isFormVisible && (
                            <FormSection 
                                id="form-section"
                                langData={langData} 
                                currentLang={currentLang}
                                onLangChange={handleLanguageChange}
                                onFormSubmitSuccess={navigateToResults}
                                className="scroll-mt-24"
                            />
                        )}
                    </>
                );
        }
    };

    return (
        <div className="min-h-screen flex flex-col font-lato text-gray-800">
            <Header 
                langData={langData} 
                currentLang={currentLang}
                onLangChange={handleLanguageChange}
                onNavigate={handleNavigate} // smooth scroll ke liye custom handler
                currentPage={currentPage}
            />
            <main className="flex-grow">
                {renderPage()}
            </main>
            <Footer langData={langData} />
        </div>
    );
}

export default App;
